import React, { useState, useCallback } from 'react';
import { CodeBracketIcon, AcademicCapIcon, ChatBubbleLeftRightIcon, ArrowPathIcon, SparklesIcon, HeartIcon, InformationCircleIcon, CogIcon, PlusIcon, TrashIcon, ClockIcon } from '@heroicons/react/24/outline';
import Particles from "react-tsparticles";
import { loadSlim } from "tsparticles-slim";
import type { Container, Engine } from "tsparticles-engine";
import About from './About';

interface Message {
  role: 'user' | 'assistant';
  content: string;
}

interface ModeConfig {
  model: string;
  prompt: string;
  temperature: number;
}

function App() {
  const [messages, setMessages] = useState<Message[]>([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [selectedMode, setSelectedMode] = useState<string>('normal');
  const [showAbout, setShowAbout] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [chatHistory, setChatHistory] = useState([
    { id: 1, title: 'Conversa sobre IA', date: '2024-03-20' },
    { id: 2, title: 'Dúvidas de Programação', date: '2024-03-19' },
    { id: 3, title: 'Estudos de Matemática', date: '2024-03-18' },
  ]);

  const particlesInit = useCallback(async (engine: Engine) => {
    await loadSlim(engine);
  }, []);

  const particlesLoaded = useCallback(async (container: Container | undefined) => {
    console.log(container);
  }, []);

  const modeConfigs: Record<string, ModeConfig> = {
    normal: {
      model: 'deepseek/deepseek-chat-v3-0324:free',
      prompt: 'Olá! Como você pode me ajudar hoje?',
      temperature: 0.7
    },
    programacao: {
      model: 'deepseek/deepseek-chat-v3-0324:free',
      prompt: 'Você é um assistente especializado em programação. Como posso ajudar?',
      temperature: 0.3
    },
    estudar: {
      model: 'deepseek/deepseek-chat-v3-0324:free',
      prompt: 'Você é um tutor educacional. Em que posso ajudar com seus estudos?',
      temperature: 0.7
    },
    conversar: {
      model: 'deepseek/deepseek-chat-v3-0324:free',
      prompt: 'Olá! Vamos conversar sobre qualquer assunto.',
      temperature: 0.5
    },
    responder: {
      model: 'deepseek/deepseek-chat-v3-0324:free',
      prompt: 'Quero que me responda algumas perguntas, lá vai.',
      temperature: 0.6
    }
  };
  const formatMessage = (content: string) => {
    // Substitui **texto** por texto em negrito
    let formatted = content.replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>');

    // Substitui *texto* por texto em itálico
    formatted = formatted.replace(/\*(.*?)\*/g, '<em>$1</em>');

    // Substitui # Título por título formatado
    formatted = formatted.replace(/^# (.*$)/gm, '<h1 class="text-2xl font-bold mb-4">$1</h1>');
    formatted = formatted.replace(/^## (.*$)/gm, '<h2 class="text-xl font-bold mb-3">$1</h2>');
    formatted = formatted.replace(/^### (.*$)/gm, '<h3 class="text-lg font-bold mb-2">$1</h3>');

    // Substitui quebras de linha por <br>
    formatted = formatted.replace(/\n/g, '<br>');

    // Adiciona espaçamento entre parágrafos
    formatted = formatted.replace(/<br><br>/g, '</p><p>');

    return `<p>${formatted}</p>`;
  };

  const handleModeSelect = (mode: string) => {
    setSelectedMode(mode);
    setInput(modeConfigs[mode].prompt);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!input.trim()) return;

    const userMessage: Message = { role: 'user', content: input };
    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsLoading(true);
    setError(null);

    try {
      const currentMode = modeConfigs[selectedMode];
      const requestBody = {
        model: currentMode.model,
        messages: [...messages, userMessage].map(msg => ({
          role: msg.role,
          content: msg.content
        })),
        temperature: currentMode.temperature,
        max_tokens: 1000
      };

      console.log('Enviando requisição:', JSON.stringify(requestBody, null, 2));

      const response = await fetch('https://openrouter.ai/api/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer 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',
          'HTTP-Referer': window.location.origin,
          'X-Title': 'SEONN'
        },
        body: JSON.stringify(requestBody)
      });

      const data = await response.json();
      console.log('Resposta da API:', JSON.stringify(data, null, 2));

      if (!response.ok) {
        throw new Error(data.error?.message || 'Erro na requisição à API');
      }

      if (!data.choices?.[0]?.message?.content) {
        console.error('Resposta inválida:', data);
        throw new Error('Formato de resposta inválido da API');
      }

      const assistantMessage: Message = {
        role: 'assistant',
        content: data.choices[0].message.content
      };
      setMessages(prev => [...prev, assistantMessage]);
    } catch (error) {
      console.error('Erro detalhado:', error);
      setError(error instanceof Error ? error.message : 'Erro ao processar a resposta da IA');
    } finally {
      setIsLoading(false);
    }
  };

  const handleDonate = () => {
    window.open('https://www.paypal.com/donate/?hosted_button_id=SEU_ID_DO_PAYPAL', '_blank');
  };

  // Função para fechar a sidebar ao clicar fora
  const handleClickOutside = (e: React.MouseEvent) => {
    if (isSidebarOpen && !(e.target as HTMLElement).closest('.sidebar')) {
      setIsSidebarOpen(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 to-black text-white flex" onClick={handleClickOutside}>
      {/* Sidebar Ultra Minimalista */}
      <div className={`fixed inset-y-0 left-0 z-50 w-56 bg-gray-900/95 backdrop-blur-xl transform transition-transform duration-300 ease-in-out ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full'} sidebar`}>
        <div className="flex flex-col h-full">
          {/* Logo e Toggle */}
          <div className="p-4 border-b border-gray-800 flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <div className="w-8 h-8 bg-gradient-to-r from-indigo-500 to-purple-500 rounded-lg transform rotate-45"></div>
              <span className="text-xl font-bold bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 text-transparent bg-clip-text">SEONN</span>
            </div>
            <button
              onClick={() => setIsSidebarOpen(false)}
              className="p-1.5 rounded-lg text-gray-400 hover:text-white hover:bg-gray-800/50 transition-colors"
            >
              <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>

          {/* Nova Conversa */}
          <div className="p-3">
            <button className="w-full flex items-center justify-center space-x-2 p-2 rounded-lg bg-indigo-500/20 text-indigo-400 hover:bg-indigo-500/30 transition-colors">
              <PlusIcon className="w-4 h-4" />
              <span className="text-sm">Nova</span>
            </button>
          </div>

          {/* Histórico de Chats */}
          <div className="flex-1 overflow-y-auto px-3">
            <div className="space-y-1">
              {chatHistory.map((chat) => (
                <button
                  key={chat.id}
                  className="w-full flex items-center space-x-2 p-2 rounded-lg text-gray-400 hover:bg-gray-800/50 transition-colors group"
                >
                  <ClockIcon className="w-4 h-4 flex-shrink-0" />
                  <div className="flex-1 min-w-0">
                    <p className="text-sm truncate">{chat.title}</p>
                  </div>
                  <button className="opacity-0 group-hover:opacity-100 p-1 hover:bg-red-500/20 rounded transition-all">
                    <TrashIcon className="w-3 h-3 text-red-400" />
                  </button>
                </button>
              ))}
            </div>
          </div>

          {/* Configurações */}
          <div className="p-3 border-t border-gray-800">
            <button className="w-full flex items-center justify-center space-x-2 p-2 rounded-lg text-gray-400 hover:bg-gray-800/50 transition-colors">
              <CogIcon className="w-4 h-4" />
              <span className="text-sm">Config</span>
            </button>
          </div>
        </div>
      </div>

      {/* Toggle Sidebar Button - Só aparece quando fechada */}
      {!isSidebarOpen && (
        <button
          onClick={() => setIsSidebarOpen(true)}
          className="fixed top-4 left-4 z-50 p-2 rounded-lg bg-gray-900/95 backdrop-blur-xl text-gray-400 hover:text-white transition-colors"
        >
          <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16M4 18h16" />
          </svg>
        </button>
      )}

      {/* Main Content */}
      <div className="flex-1 transition-all duration-300">
        {showAbout ? (
          <About onBack={() => setShowAbout(false)} />
        ) : (
          <div className="h-screen flex flex-col">
            {/* Header */}
            <div className="flex-none p-4">
              <div className="text-center">
                <div className="flex items-center justify-center space-x-3 mb-2">
                  <div className="w-12 h-12 bg-gradient-to-r from-indigo-500 to-purple-500 rounded-lg transform rotate-45 animate-pulse shadow-lg"></div>
                  <div className="w-12 h-12 bg-gradient-to-r from-purple-500 to-pink-500 rounded-lg transform -rotate-45 animate-pulse shadow-lg" style={{ animationDelay: '0.5s' }}></div>
                </div>
                <h1 className="text-4xl font-bold bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 text-transparent bg-clip-text animate-gradient">
                  SEONN
                </h1>
                <p className="text-gray-400 text-sm mt-1">Sua IA simples e amigável!</p>
              </div>
            </div>

            {/* Aviso discreto */}
            <div className="absolute top-4 right-4 flex items-center text-red-300 text-xs opacity-80 hover:opacity-100 transition-opacity duration-300 bg-white/10 backdrop-blur-md px-3 py-1.5 rounded-full shadow-lg">
              <InformationCircleIcon className="w-3.5 h-3.5 mr-1.5" />
              <span>Conversas não são salvas.</span>
            </div>

            {/* Chat Area */}
            <div className="flex-1 overflow-y-auto px-4 py-2 space-y-3">
              {messages.map((message, index) => (
                <div
                  key={index}
                  className={`p-3 rounded-xl shadow-sm ${
                    message.role === 'user'
                      ? 'bg-gradient-to-r from-indigo-500 to-purple-500 text-white ml-auto'
                      : 'bg-white/10 backdrop-blur-md text-gray-100'
                  } max-w-[85%]`}
                >
                  {message.role === 'user' ? (
                    message.content
                  ) : (
                    <div 
                      className="prose prose-invert prose-sm max-w-none"
                      dangerouslySetInnerHTML={{ __html: formatMessage(message.content) }}
                    />
                  )}
                </div>
              ))}
              {isLoading && (
                <div className="bg-white/10 backdrop-blur-md text-gray-100 p-3 rounded-xl max-w-[85%] shadow-sm">
                  <div className="flex items-center space-x-2">
                    <div className="w-2 h-2 bg-indigo-500 rounded-full animate-bounce"></div>
                    <div className="w-2 h-2 bg-purple-500 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                    <div className="w-2 h-2 bg-pink-500 rounded-full animate-bounce" style={{ animationDelay: '0.4s' }}></div>
                  </div>
                </div>
              )}
              {error && (
                <div className="bg-red-500/20 backdrop-blur-md border border-red-500/30 text-red-200 px-4 py-2 rounded-xl shadow-sm" role="alert">
                  <strong className="font-bold">Erro: </strong>
                  <span className="block sm:inline">{error}</span>
                </div>
              )}
            </div>

            {/* Mode Selection */}
            <div className="flex-none flex justify-center space-x-4 p-2">
              <button
                onClick={() => handleModeSelect('normal')}
                className={`p-2 rounded-lg transition-all duration-200 ${
                  selectedMode === 'normal'
                    ? 'bg-gradient-to-r from-indigo-500 to-purple-500 text-white shadow-lg'
                    : 'bg-white/10 backdrop-blur-md text-gray-100 hover:bg-white/20'
                } shadow-md`}
                title="Modo Normal"
              >
                <SparklesIcon className="w-5 h-5" />
              </button>
              <button
                onClick={() => handleModeSelect('programacao')}
                className={`p-2 rounded-lg transition-all duration-200 ${
                  selectedMode === 'programacao'
                    ? 'bg-gradient-to-r from-indigo-500 to-purple-500 text-white shadow-lg'
                    : 'bg-white/10 backdrop-blur-md text-gray-100 hover:bg-white/20'
                } shadow-md`}
                title="Modo Programação"
              >
                <CodeBracketIcon className="w-5 h-5" />
              </button>
              <button
                onClick={() => handleModeSelect('estudar')}
                className={`p-2 rounded-lg transition-all duration-200 ${
                  selectedMode === 'estudar'
                    ? 'bg-gradient-to-r from-indigo-500 to-purple-500 text-white shadow-lg'
                    : 'bg-white/10 backdrop-blur-md text-gray-100 hover:bg-white/20'
                } shadow-md`}
                title="Modo Estudo"
              >
                <AcademicCapIcon className="w-5 h-5" />
              </button>
              <button
                onClick={() => handleModeSelect('conversar')}
                className={`p-2 rounded-lg transition-all duration-200 ${
                  selectedMode === 'conversar'
                    ? 'bg-gradient-to-r from-indigo-500 to-purple-500 text-white shadow-lg'
                    : 'bg-white/10 backdrop-blur-md text-gray-100 hover:bg-white/20'
                } shadow-md`}
                title="Modo Conversa"
              >
                <ChatBubbleLeftRightIcon className="w-5 h-5" />
              </button>
              <button
                onClick={() => handleModeSelect('responder')}
                className={`p-2 rounded-lg transition-all duration-200 ${
                  selectedMode === 'responder'
                    ? 'bg-gradient-to-r from-indigo-500 to-purple-500 text-white shadow-lg'
                    : 'bg-white/10 backdrop-blur-md text-gray-100 hover:bg-white/20'
                } shadow-md`}
                title="Modo Respostas"
              >
                <ArrowPathIcon className="w-5 h-5" />
              </button>
            </div>

            {/* Input Area */}
            <div className="flex-none p-4">
              <form onSubmit={handleSubmit} className="flex gap-3">
                <input
                  type="text"
                  value={input}
                  onChange={(e) => setInput(e.target.value)}
                  placeholder={`Modo ${selectedMode.charAt(0).toUpperCase() + selectedMode.slice(1)} - Digite sua mensagem...`}
                  className="flex-1 p-3 rounded-xl border border-gray-200 bg-white/10 backdrop-blur-md text-gray-100 placeholder-gray-400 focus:outline-none focus:border-indigo-500 focus:ring-2 focus:ring-indigo-200 transition-all duration-200"
                />
                <button
                  type="submit"
                  disabled={isLoading}
                  className="bg-gradient-to-r from-indigo-500 to-purple-500 text-white px-6 py-3 rounded-xl hover:from-indigo-600 hover:to-purple-600 disabled:opacity-50 transition-all duration-200 shadow-lg hover:shadow-xl"
                >
                  Enviar
                </button>
              </form>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
