import React, { useState, useCallback } from 'react';
import { CodeBracketIcon, AcademicCapIcon, ChatBubbleLeftRightIcon, ArrowPathIcon, SparklesIcon, HeartIcon, InformationCircleIcon } from '@heroicons/react/24/outline';
import Particles from "react-tsparticles";
import { loadSlim } from "tsparticles-slim";
import type { Container, Engine } from "tsparticles-engine";
import About from './About';

interface Message {
  role: 'user' | 'assistant';
  content: string;
}

interface ModeConfig {
  model: string;
  prompt: string;
  temperature: number;
}

function App() {
  const [messages, setMessages] = useState<Message[]>([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [selectedMode, setSelectedMode] = useState<string>('normal');
  const [showAbout, setShowAbout] = useState(false);

  const particlesInit = useCallback(async (engine: Engine) => {
    await loadSlim(engine);
  }, []);

  const particlesLoaded = useCallback(async (container: Container | undefined) => {
    console.log(container);
  }, []);

  const modeConfigs: Record<string, ModeConfig> = {
    normal: {
      model: 'deepseek/deepseek-chat-v3-0324:free',
      prompt: 'Olá! Como você pode me ajudar hoje?',
      temperature: 0.7
    },
    programacao: {
      model: 'deepseek/deepseek-chat-v3-0324:free',
      prompt: 'Você é um assistente especializado em programação. Como posso ajudar?',
      temperature: 0.3
    },
    estudar: {
      model: 'deepseek/deepseek-chat-v3-0324:free',
      prompt: 'Você é um tutor educacional. Em que posso ajudar com seus estudos?',
      temperature: 0.7
    },
    conversar: {
      model: 'deepseek/deepseek-chat-v3-0324:free',
      prompt: 'Olá! Vamos conversar sobre qualquer assunto.',
      temperature: 0.5
    },
    responder: {
      model: 'deepseek/deepseek-chat-v3-0324:free',
      prompt: 'Quero que me responda algumas perguntas, lá vai.',
      temperature: 0.6
    }
  };
  const formatMessage = (content: string) => {
    // Substitui **texto** por texto em negrito
    let formatted = content.replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>');

    // Substitui *texto* por texto em itálico
    formatted = formatted.replace(/\*(.*?)\*/g, '<em>$1</em>');

    // Substitui # Título por título formatado
    formatted = formatted.replace(/^# (.*$)/gm, '<h1 class="text-2xl font-bold mb-4">$1</h1>');
    formatted = formatted.replace(/^## (.*$)/gm, '<h2 class="text-xl font-bold mb-3">$1</h2>');
    formatted = formatted.replace(/^### (.*$)/gm, '<h3 class="text-lg font-bold mb-2">$1</h3>');

    // Substitui quebras de linha por <br>
    formatted = formatted.replace(/\n/g, '<br>');

    // Adiciona espaçamento entre parágrafos
    formatted = formatted.replace(/<br><br>/g, '</p><p>');

    return `<p>${formatted}</p>`;
  };

  const handleModeSelect = (mode: string) => {
    setSelectedMode(mode);
    setInput(modeConfigs[mode].prompt);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!input.trim()) return;

    const userMessage: Message = { role: 'user', content: input };
    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsLoading(true);
    setError(null);

    try {
      const currentMode = modeConfigs[selectedMode];
      const requestBody = {
        model: currentMode.model,
        messages: [...messages, userMessage].map(msg => ({
          role: msg.role,
          content: msg.content
        })),
        temperature: currentMode.temperature,
        max_tokens: 1000
      };

      console.log('Enviando requisição:', JSON.stringify(requestBody, null, 2));

      const response = await fetch('https://openrouter.ai/api/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer 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',
          'HTTP-Referer': window.location.origin,
          'X-Title': 'SEONN'
        },
        body: JSON.stringify(requestBody)
      });

      const data = await response.json();
      console.log('Resposta da API:', JSON.stringify(data, null, 2));

      if (!response.ok) {
        throw new Error(data.error?.message || 'Erro na requisição à API');
      }

      if (!data.choices?.[0]?.message?.content) {
        console.error('Resposta inválida:', data);
        throw new Error('Formato de resposta inválido da API');
      }

      const assistantMessage: Message = {
        role: 'assistant',
        content: data.choices[0].message.content
      };
      setMessages(prev => [...prev, assistantMessage]);
    } catch (error) {
      console.error('Erro detalhado:', error);
      setError(error instanceof Error ? error.message : 'Erro ao processar a resposta da IA');
    } finally {
      setIsLoading(false);
    }
  };

  const handleDonate = () => {
    window.open('https://www.paypal.com/donate/?hosted_button_id=SEU_ID_DO_PAYPAL', '_blank');
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 to-black text-white">
      {showAbout ? (
        <About onBack={() => setShowAbout(false)} />
      ) : (
        <div className="container mx-auto px-4 py-8 relative min-h-screen">
          <div className="fixed bottom-4 right-4 z-50">
            <button
              onClick={() => setShowAbout(true)}
              className="group relative inline-flex items-center justify-center px-6 py-3 overflow-hidden font-medium text-white transition duration-300 ease-out rounded-full shadow-lg bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 hover:from-pink-500 hover:via-purple-500 hover:to-indigo-500"
            >
              <span className="absolute inset-0 flex items-center justify-center w-full h-full text-white duration-300 -translate-x-full bg-gradient-to-r from-pink-500 via-purple-500 to-indigo-500 group-hover:translate-x-0 ease">
                <InformationCircleIcon className="w-5 h-5 mr-2" />
                Sobre
              </span>
              <span className="absolute flex items-center justify-center w-full h-full text-white transition-all duration-300 transform group-hover:translate-x-full ease">
                <InformationCircleIcon className="w-5 h-5 mr-2" />
                Sobre
              </span>
              <span className="relative invisible">Sobre</span>
            </button>
          </div>
          <Particles
            id="tsparticles"
            init={particlesInit}
            loaded={particlesLoaded}
            options={{
              fullScreen: {
                enable: true,
                zIndex: -1
              },
              background: {
                color: {
                  value: "transparent",
                },
              },
              fpsLimit: 60,
              interactivity: {
                events: {
                  onClick: {
                    enable: true,
                    mode: "push",
                  },
                  onHover: {
                    enable: true,
                    mode: "repulse",
                  },
                  resize: true,
                },
                modes: {
                  push: {
                    quantity: 4,
                  },
                  repulse: {
                    distance: 200,
                    duration: 0.4,
                  },
                },
              },
              particles: {
                color: {
                  value: ["#6366f1", "#8b5cf6", "#ec4899"],
                },
                links: {
                  color: "#6366f1",
                  distance: 150,
                  enable: true,
                  opacity: 0.4,
                  width: 1,
                },
                collisions: {
                  enable: true,
                },
                move: {
                  direction: "none",
                  enable: true,
                  outModes: {
                    default: "bounce",
                  },
                  random: true,
                  speed: 2,
                  straight: false,
                },
                number: {
                  density: {
                    enable: true,
                    area: 800,
                  },
                  value: 100,
                },
                opacity: {
                  value: 0.5,
                },
                shape: {
                  type: ["circle", "triangle"],
                },
                size: {
                  value: { min: 2, max: 5 },
                },
              },
              detectRetina: true,
            }}
          />
          <div className="max-w-2xl mx-auto w-full p-4 flex-1 flex flex-col">
            <div className="text-center mb-4 mt-4">
              <div className="flex items-center justify-center space-x-2 mb-2">
                <div className="w-12 h-12 bg-gradient-to-r from-indigo-500 to-purple-500 rounded-lg transform rotate-45 animate-pulse shadow-lg"></div>
                <div className="w-12 h-12 bg-gradient-to-r from-purple-500 to-pink-500 rounded-lg transform -rotate-45 animate-pulse shadow-lg" style={{ animationDelay: '0.5s' }}></div>
              </div>
              <h1 className="text-5xl font-bold bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 text-transparent bg-clip-text animate-gradient">
                SEONN
              </h1>
              <p className="text-gray-600 mt-2 text-sm">Sua IA simples e amigável!</p>
              <button
                onClick={handleDonate}
                className="mt-4 group relative inline-flex items-center justify-center px-6 py-2 overflow-hidden font-medium text-indigo-600 transition duration-300 ease-out border-2 border-indigo-500 rounded-full shadow-md hover:bg-gradient-to-r hover:from-indigo-500 hover:to-purple-500 hover:text-white"
              >
                <span className="absolute inset-0 flex items-center justify-center w-full h-full text-white duration-300 -translate-x-full bg-gradient-to-r from-indigo-500 to-purple-500 group-hover:translate-x-0 ease">
                  <HeartIcon className="w-5 h-5 mr-2" />
                  Apoiar
                </span>
                <span className="absolute flex items-center justify-center w-full h-full text-indigo-500 transition-all duration-300 transform group-hover:translate-x-full ease">
                  <HeartIcon className="w-5 h-5 mr-2" />
                  Apoiar
                </span>
                <span className="relative invisible">Apoiar</span>
              </button>
            </div>

            {/* Aviso discreto */}
            <div className="absolute top-2 right-4 flex items-center text-red-300 text-xs opacity-80 hover:opacity-100 transition-opacity duration-300 bg-white/50 backdrop-blur-sm px-2 py-1 rounded-full shadow-sm">
              <InformationCircleIcon className="w-3.5 h-3.5 mr-1" />
              <span>Conversas não são salvas.</span>
            </div>

            <div className="flex-1 overflow-y-auto mb-4 space-y-4">
              {messages.map((message, index) => (
                <div
                  key={index}
                  className={`p-4 rounded-lg shadow-sm ${
                    message.role === 'user'
                      ? 'bg-gradient-to-r from-indigo-500 to-purple-500 text-white ml-auto'
                      : 'bg-white text-gray-800'
                  } max-w-[80%]`}
                >
                  {message.role === 'user' ? (
                    message.content
                  ) : (
                    <div 
                      className="prose prose-sm max-w-none"
                      dangerouslySetInnerHTML={{ __html: formatMessage(message.content) }}
                    />
                  )}
                </div>
              ))}
              {isLoading && (
                <div className="bg-white text-gray-800 p-4 rounded-lg max-w-[80%] shadow-sm">
                  <div className="flex items-center space-x-2">
                    <div className="w-2 h-2 bg-indigo-500 rounded-full animate-bounce"></div>
                    <div className="w-2 h-2 bg-purple-500 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                    <div className="w-2 h-2 bg-pink-500 rounded-full animate-bounce" style={{ animationDelay: '0.4s' }}></div>
                  </div>
                </div>
              )}
              {error && (
                <div className="bg-red-50 border border-red-200 text-red-600 px-4 py-3 rounded-lg shadow-sm" role="alert">
                  <strong className="font-bold">Erro: </strong>
                  <span className="block sm:inline">{error}</span>
                </div>
              )}
            </div>

            <div className="flex justify-center space-x-4 mb-4">
              <button
                onClick={() => handleModeSelect('normal')}
                className={`p-3 rounded-lg transition-all duration-200 ${
                  selectedMode === 'normal'
                    ? 'bg-gradient-to-r from-indigo-500 to-purple-500 text-white'
                    : 'bg-white text-gray-600 hover:bg-gray-50'
                } shadow-sm`}
                title="Modo Normal"
              >
                <SparklesIcon className="w-6 h-6" />
              </button>
              <button
                onClick={() => handleModeSelect('programacao')}
                className={`p-3 rounded-lg transition-all duration-200 ${
                  selectedMode === 'programacao'
                    ? 'bg-gradient-to-r from-indigo-500 to-purple-500 text-white'
                    : 'bg-white text-gray-600 hover:bg-gray-50'
                } shadow-sm`}
                title="Modo Programação"
              >
                <CodeBracketIcon className="w-6 h-6" />
              </button>
              <button
                onClick={() => handleModeSelect('estudar')}
                className={`p-3 rounded-lg transition-all duration-200 ${
                  selectedMode === 'estudar'
                    ? 'bg-gradient-to-r from-indigo-500 to-purple-500 text-white'
                    : 'bg-white text-gray-600 hover:bg-gray-50'
                } shadow-sm`}
                title="Modo Estudo"
              >
                <AcademicCapIcon className="w-6 h-6" />
              </button>
              <button
                onClick={() => handleModeSelect('conversar')}
                className={`p-3 rounded-lg transition-all duration-200 ${
                  selectedMode === 'conversar'
                    ? 'bg-gradient-to-r from-indigo-500 to-purple-500 text-white'
                    : 'bg-white text-gray-600 hover:bg-gray-50'
                } shadow-sm`}
                title="Modo Conversa"
              >
                <ChatBubbleLeftRightIcon className="w-6 h-6" />
              </button>
              <button
                onClick={() => handleModeSelect('responder')}
                className={`p-3 rounded-lg transition-all duration-200 ${
                  selectedMode === 'responder'
                    ? 'bg-gradient-to-r from-indigo-500 to-purple-500 text-white'
                    : 'bg-white text-gray-600 hover:bg-gray-50'
                } shadow-sm`}
                title="Modo Respostas"
              >
                <ArrowPathIcon className="w-6 h-6" />
              </button>
            </div>

            <form onSubmit={handleSubmit} className="flex gap-2">
              <input
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder={`Modo ${selectedMode.charAt(0).toUpperCase() + selectedMode.slice(1)} - Digite sua mensagem...`}
                className="flex-1 p-3 rounded-lg border border-gray-200 bg-white text-gray-800 placeholder-gray-500 focus:outline-none focus:border-indigo-500 focus:ring-2 focus:ring-indigo-200 transition-all duration-200"
              />
              <button
                type="submit"
                disabled={isLoading}
                className="bg-gradient-to-r from-indigo-500 to-purple-500 text-white px-6 py-3 rounded-lg hover:from-indigo-600 hover:to-purple-600 disabled:opacity-50 transition-all duration-200 shadow-sm"
              >
                Enviar
              </button>
            </form>
            <p className="mt-2 text-sm text-gray-600 text-center">
              Conheça mais sobre nossa missão de tornar a IA acessível para todos
            </p>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
